mapper.select:
		select 
			   count(*)
		  from great
         where great_no = #{ greatNo }
           and user_no = #{ userNo }

mapper.delete:
		delete 
		  from great
         where great_no = #{ greatNo }
           and user_no = #{ userNo }

mapper.insert:
insert
  into great
  (
    great_no
  , user_no
  )
  values
  (
    #{ greatNo }
  , #{ userNo }
  )

mapper.select:
		select count(*) from basket
 		 where ref_no = #{ refNo }
   	 	   and user_no = #{ userNo }

mapper.update:
update basket 
   set count = count + #{ count }
 where ref_no = #{ refNo }
   and user_no = #{ userNo }

mapper.insert:
insert 
  into basket
  (
    ref_no
  , user_no
  , count
  )
  values
  (
    #{ refNo }
  , #{ userNo }
  , #{ count }
  )

mapper.delete:
		delete 
		  from basket
         where basket_no = #{ refNo }
           and user_no = #{ userNo }
