mapper.select:
		select 
			   count(*)
		  from great
         where great_no = #{ greatNo }
           and user_no = #{ userNo }

mapper.delete:
		delete 
		  from great
         where great_no = #{ greatNo }
           and user_no = #{ userNo }

mapper.insert:
insert
  into great
  (
    great_no
  , user_no
  )
  values
  (
    #{ greatNo }
  , #{ userNo }
  )
