mapper.insert:
INSERT 
  into gift_review
     (
       GRIV_NO
     , GHIS_NO
     , GRIV_CONTENT
     , GRIV_GRADE
     )
values
     (
       seq_griv.nextval
     , #{ghisNo}
     , #{grivContent}
     , #{grivGrade}
     )

insert
  into gift_img
     (
       REVIEW_NO
     , G_ORIGIN_NAME
     , G_CHANGE_NAME
     , G_FILE_PATH
     )
values
     (
       seq_griv.CURRVAL
     , #{originName}
     , #{changeName}
     , #{filePath}
     )
